package com.arrays;

import java.util.Arrays;

public class FindTheDifference {
    public static void main(String[] args) {
        String s = "";

        String t = "";
        char[] arr1 = s.toCharArray();
        char[] arr2 = t.toCharArray();

        Arrays.sort(arr1);
        Arrays.sort(arr2);

        int i = 0;
        for (; i < arr1.length; i++) {
            if (arr1[i] != arr2[i]) {
                System.out.println(arr2[i]);
                System.exit(1);
            }
        }

        System.out.println(arr2[i]);
    }
}
